import groovy.json.JsonSlurper
def response = context.expand('${Post_Customer#Response}')
def jsonSlurper = new JsonSlurper();
def jsonObj = jsonSlurper.parseText(response);
//def CustomerID_Value = jsonObj.customerID
def firstName = context.expand( '${DataSource#firstName}')


def CustomerID_Value = jsonObj.customerID
log.info CustomerID_Value
testRunner.testCase.setPropertyValue('customerID', CustomerID_Value)
